import SVGAttributeProps from "types/SVGAttributeProps.type";
import * as T from "styles/theme";

const MainLogo = ({ width = 9, height = 3 }: SVGAttributeProps) => {
  return (
    <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
      {/* 왼쪽 상단 로고 */}
      <div
        style={{
          position: "absolute",
          top: "16px",
          left: "16px",
        }}
      >
        <svg
          width={width * 16} // 로고 크기 키움
          height={height * 16}
          viewBox="0 0 116 23"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <text
            x="0"
            y="20"
            font-family="Arial, sans-serif"
            font-size="18"
            font-weight="bold"
            fill={T.blue[700]}
          >
            MIRIM.GG
          </text>
        </svg>
      </div>

      {/* 중앙 텍스트 */}
      <div style={{ marginTop: "120px", textAlign: "center" }}>
        <svg
          width={width * 20}
          height={height * 4}
          viewBox="0 0 116 23"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <text
            x="58"
            y="20"
            font-family="Arial, sans-serif"
            font-size="24"
            font-weight="bold"
            text-anchor="middle"
            fill={T.blue[700]}
          >
            MIRIM.GG
          </text>
        </svg>
      </div>
    </div>
  );
};

export default MainLogo;
